function start(){
    var personas = [new persona(), new persona(), new persona(), new persona()];
    //get the oldest person and say if there are more than 2 oldest people;
    var mayor = personas[0];
    var masDeUno = false;
    for (var i = 0; i < personas.length; i++) {
        if (personas[i].edad > mayor.edad){
            mayor = personas[i];
        }
        if (personas[i].edad == mayor.edad){
            masDeUno = true;
        }
    }
    richAlert('El mayor es: ' + mayor.nombre + '<br>' + (masDeUno ? 'Hay mas de un mayor con la misma edad' : 'No hay mas de un mayor con la misma edad'));
}

function jugador(nombre){
    this.nombre = nombre;
    this.puntos = 0;
    this.gano = false;
    this.agregar = agregar;
    this.verf = verf;
    this.impr = impr;
}
function agregar(puntos){
    this.puntos += puntos;
}
function verf(){
    if (this.puntos > 1000){
        this.gano = true;
    }
    else{
        this.gano = false;
    }
}
function impr(){
    if (this.gano){
        richAlert("El jugador " + this.nombre + " ha ganado");
    }
    else{
        richAlert("El jugador " + this.nombre + " ha perdido");
    }
}
